import React, { useState } from 'react';
import axios from 'axios';

function ChatComponent() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handlePromptChange = (e) => {
    setPrompt(e.target.value);
  };

  const handleSendPrompt = async () => {
    if (!prompt) return;
    
    setLoading(true);

    try {
      const result = await axios.post('http://localhost:9191/openai/chat', prompt, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setResponse(result.data);  // Set the AI response
    } catch (error) {
      console.error("Error fetching AI response:", error);
      setResponse("Error: Could not fetch AI response.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <textarea 
          placeholder="Type your prompt here..."
          value={prompt}
          onChange={handlePromptChange}
        />
        <button onClick={handleSendPrompt} disabled={loading}>
          {loading ? 'Sending...' : 'Send Prompt'}
        </button>

        {/* Output Response Box */}
        {response && (
          <div className="card-output">
            <h5>AI Response:</h5>
            <p>{response}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChatComponent;
