import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Container, Row, Col, Card, Spinner } from 'react-bootstrap';

const ChatComponent = () => {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!prompt.trim()) return;

    setLoading(true);
    setResponse('');

    try {
      const result = await axios.post('http://localhost:9191/openai/chat', prompt, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      setResponse(result.data);
    } catch (error) {
      console.error('Error:', error);
      setResponse('Failed to get AI response.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ height: '100vh', backgroundColor: '#121212' }}>
      <Row className="w-100">
        <Col lg={6} md={8} sm={10}>
          <Card className="p-4">
            <Card.Body>
              <h3 className="text-center text-white mb-4">AI Prompt Box</h3>

              {/* Input Section */}
              <Form.Group className="mb-3">
                <Form.Label className="text-white">Enter your prompt:</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  className="bg-dark text-white border-light"
                  placeholder="Type your prompt here..."
                />
              </Form.Group>

              {/* Submit Button */}
              <div className="text-center">
                <Button variant="primary" onClick={handleSubmit} disabled={loading}>
                  {loading ? <Spinner animation="border" size="sm" /> : 'Send'}
                </Button>
              </div>

              {/* Output Section */}
              {response && (
                <Card className="mt-4">
                  <Card.Body className="text-white bg-dark">
                    <h5 className="text-center">AI Response</h5>
                    <p>{response}</p>
                  </Card.Body>
                </Card>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default ChatComponent;
