import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatComponent() {
    const [prompt, setPrompt] = useState('');
    const [response, setResponse] = useState('');
    const [loading, setLoading] = useState(false);
    const [displayedResponse, setDisplayedResponse] = useState('');

    const handlePromptChange = (e) => {
        setPrompt(e.target.value);
    };

    const handleSendPrompt = async () => {
        if (!prompt.trim()) return;  // Prevent sending empty prompts

        setLoading(true);
        setResponse('');
        setDisplayedResponse('');

        try {
            const result = await axios.post(
                'http://localhost:9191/openai/chat', 
                { prompt }, // Send as JSON
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );

            const aiMessage = typeof result.data === "string" ? JSON.parse(result.data).choices[0].message.content : result.data.choices[0].message.content;

            setResponse(aiMessage);
        } catch (error) {
            console.error("Error fetching AI response:", error);
            setResponse("Error: Could not fetch AI response.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        let index = 0;
        if (response) {
            const intervalId = setInterval(() => {
                setDisplayedResponse(prev => prev + response[index]);
                index++;
                if (index >= response.length) clearInterval(intervalId);
            }, 40); // Speed of typing (ms)
            return () => clearInterval(intervalId);
        }
    }, [response]);

    return (
        <div className="container">
            <div className="card">
                <textarea 
                    placeholder="Type your prompt here..."
                    value={prompt}
                    onChange={handlePromptChange}
                />
                <button onClick={handleSendPrompt} disabled={loading}>
                    {loading ? <div className="spinner"></div> : 'Send Prompt'}
                </button>

                {displayedResponse && (
                    <div className="card-output">
                        <h5>AI Response:</h5>
                        <p>{displayedResponse}</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default ChatComponent;
