import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatComponent() {
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [displayedResponse, setDisplayedResponse] = useState(''); // ðŸ‘ˆ New state for typing effect
  const [loading, setLoading] = useState(false);

  const handlePromptChange = (e) => {
    setPrompt(e.target.value);
  };

  const handleSendPrompt = async () => {
    if (!prompt.trim()) return;

    setLoading(true);
    setResponse('');
    setDisplayedResponse('');

    try {
      const result = await axios.post('http://localhost:9191/openai/chat', prompt, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log('Backend response:', result.data); // Optional: debug

      const data = typeof result.data === "string" ? JSON.parse(result.data) : result.data;
      const aiMessage = data.choices[0].message.content;

      setResponse(aiMessage); // âœ… Set the full response
    } catch (error) {
      console.error("Error fetching AI response:", error);
      setResponse("Error: Could not fetch AI response.");
    } finally {
      setLoading(false);
    }
  };

  // Typing animation effect
  useEffect(() => {
    let index = 0;
    if (response) {
      const interval = setInterval(() => {
        setDisplayedResponse((prev) => prev + response.charAt(index));
        index++;
        if (index >= response.length) {
          clearInterval(interval);
        }
      }, 30); // Typing speed (smaller = faster)
      return () => clearInterval(interval);
    }
  }, [response]);

  return (
    <div className="container">
      <div className="card">
        <textarea 
          placeholder="Type your prompt here..."
          value={prompt}
          onChange={handlePromptChange}
        />
        <button onClick={handleSendPrompt} disabled={loading}>
          {loading ? <div className="spinner"></div> : 'Send Prompt'}
        </button>

        {/* Output Response Box */}
        {displayedResponse && (
          <div className="card-output">
            <h5>AI Response:</h5>
            <p>{displayedResponse}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default ChatComponent;
