import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatComponent() {
    const [tabs, setTabs] = useState([{ prompt: '', response: '', isLoading: false, displayedResponse: '', isPaused: false, intervalId: null }]);
    const [activeTabIndex, setActiveTabIndex] = useState(0);

    const handlePromptChange = (e) => {
        const newTabs = [...tabs];
        newTabs[activeTabIndex].prompt = e.target.value;
        setTabs(newTabs);
    };

    const handleSendPrompt = async () => {
        const currentTab = tabs[activeTabIndex];
        const promptText = currentTab.prompt;
        if (!promptText.trim()) return;

        const newTabs = [...tabs];
        newTabs[activeTabIndex].isLoading = true;
        newTabs[activeTabIndex].displayedResponse = '';
        setTabs(newTabs);

        try {
            const result = await axios.post('http://localhost:9191/openai/chat',
                { prompt: promptText },
                {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                }
            );

            const data = typeof result.data === "string" ? JSON.parse(result.data) : result.data;
            const aiMessage = data.choices[0].message.content;

            const cleanedResponse = aiMessage.trim();
            newTabs[activeTabIndex].response = cleanedResponse;
            setTabs(newTabs);

            startTypingEffect(cleanedResponse);
        } catch (error) {
            console.error("Error fetching AI response:", error);
            const newTabsWithError = [...tabs];
            newTabsWithError[activeTabIndex].response = "Error: Could not fetch AI response.";
            setTabs(newTabsWithError);
        } finally {
            newTabs[activeTabIndex].isLoading = false;
            setTabs(newTabs);
        }
    };

    const handlePause = () => {
        const newTabs = [...tabs];
        const currentTab = newTabs[activeTabIndex];
        if (currentTab.intervalId) {
            clearInterval(currentTab.intervalId);
            currentTab.intervalId = null;
        }
        currentTab.isPaused = true;
        setTabs(newTabs);
    };

    const handleResume = () => {
        const newTabs = [...tabs];
        const currentTab = newTabs[activeTabIndex];
        currentTab.isPaused = false;
        setTabs(newTabs);
        startTypingEffect(currentTab.response);
    };

    const startTypingEffect = (responseText) => {
        let index = 0;
        const id = setInterval(() => {
            const newTabs = [...tabs];
            const currentTab = newTabs[activeTabIndex];
            currentTab.displayedResponse += responseText[index];
            setTabs(newTabs);

            index++;
            if (index >= responseText.length) clearInterval(id);
        }, 40);
    };

    const handleTabChange = (index) => {
        setActiveTabIndex(index);
    };

    const handleAddTab = () => {
        const newTabs = [...tabs, { prompt: '', response: '', isLoading: false, displayedResponse: '', isPaused: false, intervalId: null }];
        setTabs(newTabs);
        setActiveTabIndex(newTabs.length - 1);  // Switch to the new tab
    };

    const handleRemoveTab = (index) => {
        const newTabs = tabs.filter((_, tabIndex) => tabIndex !== index);
        setTabs(newTabs);
        setActiveTabIndex(newTabs.length - 1);  // Switch to the last tab after removal
    };

    useEffect(() => {
        if (tabs[activeTabIndex].response && !tabs[activeTabIndex].isPaused) {
            startTypingEffect(tabs[activeTabIndex].response);
        }
        return () => {
            const currentTab = tabs[activeTabIndex];
            if (currentTab.intervalId) clearInterval(currentTab.intervalId);
        };
    }, [tabs, activeTabIndex]);

    return (
        <div className="container">
            <div className="card">
                {/* Tab navigation */}
                <div className="tabs">
                    {tabs.map((_, index) => (
                        <button
                            key={index}
                            onClick={() => handleTabChange(index)}
                            className={activeTabIndex === index ? 'active' : ''}
                        >
                            Tab {index + 1}
                        </button>
                    ))}
                </div>

                {/* Add and Remove Tabs */}
                <div className="tab-actions">
                    <button onClick={handleAddTab}>+ Add Tab</button>
                    {tabs.length > 1 && <button onClick={() => handleRemoveTab(activeTabIndex)}>Remove Tab</button>}
                </div>

                {/* Prompt Input */}
                <textarea
                    placeholder="Type your prompt here..."
                    value={tabs[activeTabIndex].prompt}
                    onChange={handlePromptChange}
                />
                <button onClick={handleSendPrompt} disabled={tabs[activeTabIndex].isLoading}>
                    {tabs[activeTabIndex].isLoading ? <div className="spinner"></div> : 'Send Prompt'}
                </button>

                {/* Display Response */}
                {tabs[activeTabIndex].displayedResponse && (
                    <div className="card-output">
                        <h5>AI Response:</h5>
                        <p>{tabs[activeTabIndex].displayedResponse}</p>
                    </div>
                )}

                {/* Pause and Resume Buttons */}
                <div className="button-group">
                    {tabs[activeTabIndex].isPaused ? (
                        <button onClick={handleResume}>Resume</button>
                    ) : (
                        <button onClick={handlePause}>Pause</button>
                    )}
                </div>
            </div>
        </div>
    );
}

export default ChatComponent;
